     "description": "Agent CA options",
     "properties": {
      "vault": {
       "description": "Use vault as the intermediate CA source",
       "properties": {
        "caBundle": {
         "description": "PEM encoded CA bundle used to validate Vault server certificate. Only used\nif the Server URL is using HTTPS protocol. This parameter is ignored for\nplain HTTP protocol connection. If not set the system root certificates\nare used to validate the TLS connection.",
         "format": "binary",
         "type": "string"
        },
        "caPath": {
         "description": "`ca_path` is the mount path of the Vault PKI backend's `sign` endpoint, e.g:\n\"my_pki_mount/sign/my-role-name\".",
         "type": "string"
        },
        "csrPath": {
         "description": "`csr_path` is the mount path of the Vault PKI backend's `generate` endpoint, e.g:\n\"my_pki_mount/intermediate/generate/exported\".\n\"exported\" is necessary here as istio needs access to the private key\nSee vault docs here: https://www.vaultproject.io/api-docs/secret/pki#parameters-4",
         "type": "string"
        },
        "kubernetesAuth": {
         "description": "Kubernetes authenticates with Vault by passing the ServiceAccount\ntoken stored in the named Secret resource to the Vault server.",
         "properties": {
          "mountPath": {
           "description": "The Vault mountPath here is the mount path to use when authenticating with\nVault. For example, setting a value to `/v1/auth/foo`, will use the path\n`/v1/auth/foo/login` to authenticate with Vault. If unspecified, the\ndefault value \"/v1/auth/kubernetes\" will be used.",
           "type": "string"
          },
          "mountedSaPath": {
           "description": "File System path to grab the service account token from.\nDefaults to /var/run/secrets/kubernetes.io/serviceaccount",
           "type": "string"
          },
          "role": {
           "description": "A required field containing the Vault Role to assume. A Role binds a\nKubernetes ServiceAccount with a set of Vault policies.",
           "type": "string"
          },
          "secretTokenKey": {
           "description": "Key to search for the sa_token\nDefault to \"token\"",
           "type": "string"
          },
          "serviceAccountRef": {
           "description": "Reference to service account, other than the one mounted to the current pod.",
           "properties": {
            "name": {
             "description": "name of the resource being referenced",
             "type": "string"
            },
            "namespace": {
             "description": "namespace of the resource being referenced",
             "type": "string"
            }
           },
           "type": "object"
          }
         },
         "type": "object"
        },
        "namespace": {
         "description": "Name of the vault namespace. Namespaces is a set of features within Vault Enterprise that allows Vault environments to support Secure Multi-tenancy. e.g: \"ns1\"\nMore about namespaces can be found [here](https://www.vaultproject.io/docs/enterprise/namespaces)",
         "type": "string"
        },
        "server": {
         "description": "Server is the connection address for the Vault server, e.g: \"https://vault.example.com:8200\".",
         "type": "string"
        },
        "tokenSecretRef": {
         "description": "TokenSecretRef authenticates with Vault by presenting a token.",
         "properties": {
