schema:
{
 "description": "VirtualGateway is the top-level object for configuring ingress into a Mesh or VirtualMesh.\n   A single VirtualGateway can apply to multiple deployed ingress pods and sidecars across meshes and clusters contained within a VirtualMesh.\n   VirtualGateways can route traffic to destination services which live in a specific cluster or mesh.\n   This allows VirtualGateways to route traffic from an ingress or sidecar in one mesh to a service in another. In order to\n   perform cross-mesh routing, the Gateway Mesh and Destination mesh must be contained in a single VirtualMesh, with federation enabled.",
 "properties": {
  "connectionHandlers": {
   "description": "Each Gateway must implement one or more ConnectionHandlers.\nA ConnectionHandler instructs the gateway how to handle clients\nwhich have connected to the specified bind address.\nTypically `connectionHandlers` will consist of a single `http` handler\nwhich serves HTTP Routes defined in a set of VirtualHosts.\nMultiple `connectionHandlers` can be specified to provide different behavior\non the same Gateway, e.g. one for TCP and one for HTTP traffic.\nNOTE: Currently having multiple connection handlers is NOT supported. Only\nexactly ONE connection handler can be specified.",
   "items": {
    "oneOf": [
     {
      "not": {
       "anyOf": [
        {
         "required": [
          "http"
         ]
        },
        {
         "required": [
          "tcp"
         ]
        }
       ]
      }
     },
     {
      "required": [
       "http"
      ]
     },
     {
      "required": [
       "tcp"
      ]
     }
    ],
    "properties": {
     "connectionMatch": {
      "description": "Additional options for matching a connection to a specific gateway.\nThis is required when more than one `connectionHandler` is specified for a single gateway.\nTypically this is used to serve different",
      "properties": {
       "serverNames": {
        "description": "If non-empty, a list of server names (e.g. SNI for TLS protocol) to consider when determining\na `connectionMatch`. Those values will be compared against the server names of a new\nconnection, when detected by one of the listener filters.\n\nThe server name will be matched against all wildcard domains, i.e. `www.example.com`\nwill be first matched against `www.example.com`, then `*.example.com`, then ``*.com`.\n\nNote that partial wildcards are not supported, and values like `*w.example.com` are invalid.",
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "transportProtocol": {
        "description": "Optional, if set this will be used as the protocol for the gateway, otherwise it will be inferred based on the following logic:\n\n- If the connectionHandler is a HTTP handler and no SslConfig is set in the connectionOptions, use \"HTTP\"\n\n- If the connectionHandler is a HTTP handler and any SslConfig is set in the connectionOptions, use \"HTTPS\"\n\n- If the connectionHandler is a TCP handler and no SslConfig is set in the connectionOptions, use \"TCP\"\n\n- If the connectionHandler is a TCP handler and any SslConfig is set in the connectionOptions, use \"TLS\"",
        "type": "string"
       }
      },
      "type": "object"
     },
     "connectionOptions": {
      "description": "Top level optional configuration for all routes on the ConnectionHandler.",
      "oneOf": [
       {
        "not": {
         "anyOf": [
          {
           "properties": {
            "sslConfig": {
             "oneOf": [
              {
               "not": {
                "anyOf": [
                 {
                  "required": [
                   "secretName"
                  ]
                 },
                 {
                  "required": [
                   "sslFiles"
                  ]
                 }
                ]
               }
              },
              {
               "required": [
                "secretName"
               ]
              },
              {
               "required": [
                "sslFiles"
               ]
              }
             ]
            }
           },
           "required": [
            "sslConfig"
           ]
          },
          {
           "required": [
            "httpsRedirect"
           ]
          }
         ]
        }
       },
       {
        "properties": {
         "sslConfig": {
          "oneOf": [
           {
            "not": {
             "anyOf": [
              {
               "required": [
                "secretName"
               ]
              },
              {
               "required": [
                "sslFiles"
               ]
              }
             ]
            }
           },
           {
            "required": [
             "secretName"
            ]
           },
           {
            "required": [
             "sslFiles"
            ]
           }
          ]
         }
        },
        "required": [
         "sslConfig"
        ]
       },
       {
        "required": [
         "httpsRedirect"
        ]
       }
      ],
      "properties": {
       "enableProxyProtocol": {
        "description": "enable PROXY protocol for this connection handler.",
        "type": "boolean"
       },
       "httpsRedirect": {
        "description": "If set to true, the load balancer will send a 301 redirect for all http connections, asking the clients to use HTTPS.",
        "type": "boolean"
       },
       "sslConfig": {
        "description": "Contains the options necessary to configure a virtual host or listener to use TLS",
        "properties": {
         "parameters": {
          "properties": {
           "cipherSuites": {
            "items": {
             "type": "string"
            },
            "type": "array"
           },
           "maximumProtocolVersion": {
            "enum": [
             "TLS_AUTO",
             "TLSv1_0",
             "TLSv1_1",
             "TLSv1_2",
             "TLSv1_3"
            ],
            "type": "string"
           },
           "minimumProtocolVersion": {
            "enum": [
             "TLS_AUTO",
             "TLSv1_0",
             "TLSv1_1",
             "TLSv1_2",
             "TLSv1_3"
            ],
            "type": "string"
           }
          },
          "type": "object"
         },
         "secretName": {
          "description": "SecretName is the name of the kubernetes secret which contains the ssl secret. Each\nGateway will look for a secret with this name on it's own local cluster in it's own namespace.",
          "type": "string"
         },
         "sslFiles": {
          "description": "SSLFiles reference paths to certificates which can be read by the proxy off of its local filesystem",
          "properties": {
           "rootCa": {
            "description": "for client cert validation. optional",
            "type": "string"
           },
           "tlsCert": {
            "type": "string"
           },
           "tlsKey": {
            "type": "string"
           }
          },
          "type": "object"
         },
         "tlsMode": {
          "description": "TLS modes enforced by the proxy",
          "enum": [
           "PASSTHROUGH",
           "SIMPLE",
           "MUTUAL",
           "AUTO_PASSTHROUGH",
           "ISTIO_MUTUAL"
          ],
          "type": "string"
         },
         "verifySubjectAltName": {
          "description": "Verify that the Subject Alternative Name in the peer certificate is one of the specified values.\nnote that a root_ca must be provided if this option is used.",
          "items": {
           "type": "string"
          },
          "type": "array"
         }
        },
        "type": "object"
       },
       "strictFilterManagement": {
        "description": "Restricts filter from being added to the corresponding Envoy Listener unless they are explicitly configured\nin the connection handler options",
        "type": "boolean"
       }
      },
      "type": "object"
     },
     "http": {
      "properties": {
       "routeConfig": {
        "items": {
         "oneOf": [
          {
           "not": {
            "anyOf": [
             {
              "required": [
               "virtualHostSelector"
              ]
             },
             {
              "required": [
               "virtualHost"
              ]
             }
            ]
           }
          },
          {
           "required": [
            "virtualHostSelector"
           ]
          },
          {
           "required": [
            "virtualHost"
           ]
          }
         ],
         "properties": {
          "virtualHost": {
           "description": "VirtualHost allows in-lining a route table directly in the Gateway Resource, for simple configs using fewer CRDs.\nNote that Kubernetes admission validation of inline virtual hosts is disabled. For production, the use of `virtualHostSelector` is recommended.",
           "type": "object",
           "x-kubernetes-preserve-unknown-fields": true
          },
          "virtualHostSelector": {
           "description": "RouteSelector is used to specify which VirtualHosts should be attached to this gateway.",
           "properties": {
            "expressions": {
             "description": "Expressions allow for more flexible object label matching, such as equality-based requirements, set-based requirements, or a combination of both.\nhttps://kubernetes.io/docs/concepts/overview/working-with-objects/labels/#equality-based-requirement",
             "items": {
              "properties": {
               "key": {
                "description": "Kubernetes label key, must conform to Kubernetes syntax requirements\nhttps://kubernetes.io/docs/concepts/overview/working-with-objects/labels/#syntax-and-character-set",
                "type": "string"
               },
               "operator": {
                "description": "The operator can only be in, notin, =, ==, !=, exists, ! (DoesNotExist), gt (GreaterThan), lt (LessThan).",
                "enum": [
                 "Equals",
                 "DoubleEquals",
                 "NotEquals",
                 "In",
                 "NotIn",
                 "Exists",
                 "DoesNotExist",
                 "GreaterThan",
                 "LessThan"
                ],
                "type": "string"
               },
               "values": {
                "items": {
                 "type": "string"
                },
                "type": "array"
               }
              },
              "type": "object"
             },
             "type": "array"
            },
            "labels": {
             "additionalProperties": {
              "type": "string"
             },
             "description": "Select objects whose labels match the ones specified here.",
             "type": "object"
            },
            "namespaces": {
             "description": "Select Objects in these namespaces. If omitted, Gloo Mesh will only select Objects in the same namespace\nas the parent resource (e.g. VirtualGateway) that owns this selector.\nThe reserved value \"*\" can be used to select objects in all namespaces watched by Gloo Mesh.",
             "items": {
              "type": "string"
             },
             "type": "array"
            }
           },
           "type": "object"
          }
         },
         "type": "object"
        },
        "type": "array"
       },
       "routeOptions": {
        "description": "HTTP Listener Options // Root level RouteTable + VirtualHost + routes level",
        "properties": {
         "csrf": {
          "description": "Configure Global CSRF options for clients connected to this Gateway.",
          "properties": {
           "additionalOrigins": {
            "description": "Specifies additional source origins that will be allowed in addition to\nthe destination origin.",
            "items": {
             "oneOf": [
              {
               "not": {
                "anyOf": [
                 {
                  "required": [
                   "exact"
                  ]
                 },
                 {
                  "required": [
                   "prefix"
                  ]
                 },
                 {
                  "required": [
                   "regex"
                  ]
                 },
                 {
                  "required": [
                   "suffix"
                  ]
                 }
                ]
               }
              },
              {
               "required": [
                "exact"
               ]
              },
              {
               "required": [
                "prefix"
               ]
              },
              {
               "required": [
                "regex"
               ]
              },
              {
               "required": [
                "suffix"
               ]
              }
             ],
             "properties": {
              "exact": {
               "description": "Exact string match.",
               "type": "string"
              },
              "ignoreCase": {
               "description": "If true, indicates the exact/prefix/suffix matching should be case insensitive. This has no effect for the regex match.",
               "type": "boolean"
              },
              "prefix": {
               "description": "Prefix-based match.",
               "type": "string"
              },
              "regex": {
               "description": "ECMAscript style regex-based match.",
               "type": "string"
              },
              "suffix": {
               "description": "Suffix-based match.",
               "type": "string"
              }
             },
             "type": "object"
            },
            "type": "array"
           },
           "filterEnabled": {
            "description": "Specifies that CSRF policies will be evaluated, tracked and enforced.",
            "type": "boolean"
           },
           "percentage": {
            "description": "Specifies the % of requests for which the CSRF filter is enabled or when shadow mode is enabled the % of requests\nevaluated and tracked, but not enforced.\n\nIf filter_enabled or shadow_enabled is true.\nEnvoy will lookup the runtime key to get the percentage of requests to filter.\n\n.. note:: This field defaults to 100",
            "format": "double",
            "type": "number"
           },
           "shadowEnabled": {
            "description": "Specifies that CSRF policies will be evaluated and tracked, but not enforced.\n\nThis is intended to be used when ``filter_enabled`` is false and will be ignored otherwise.",
            "type": "boolean"
           }
          },
          "type": "object"
         },
         "extauth": {
          "description": "Configure the Global Extauth options for clients connected to this Gateway",
          "properties": {
           "clearRouteCache": {
            "description": "Clears route cache in order to allow the external authorization service to correctly affect\nrouting decisions. Filter clears all cached routes when:\n\n1. The field is set to *true*.\n\n2. The status returned from the authorization service is a HTTP 200 or gRPC 0.\n\n3. At least one *authorization response header* is added to the client request, or is used for\naltering another client request header.",
            "type": "boolean"
           },
           "extauthzRef": {
            "description": "The destination ref for the envoy external authentication service to ask about auth decisions.\n\nThe fqdn used by envoy for extauth requests is derived from the kube service or is the first hostname\nin an external service destination.\nIMPORTANT: Envoy's extauth requests go to the port chosen from the destination that has the name or protocol of\nthe extauth service protocol (i.e., grpc or http). In practice, this means the port handling extauth requests\nshould be named grpc and/or have protocol GRPC (both case-insensitive), unless `http_service` is configured.\n\nThe provided ref will be used to search for a service of the given name/namespace on each cluster\nthat a Gateway will be created.\n\nIf omitted, Gloo Mesh will search for a service with the name ext-auth-service in each namespace on each cluster\nthat a Gateway will be created.",
            "properties": {
             "name": {
              "description": "name of the resource being referenced",
              "type": "string"
             },
             "namespace": {
              "description": "namespace of the resource being referenced",
              "type": "string"
             }
            },
            "type": "object"
           },
           "failureModeAllow": {
            "description": "In case of a failure or timeout querying the auth server, normally a request is denied.\nif this is set to true, the request will be allowed.",
            "type": "boolean"
           },
           "httpService": {
            "description": "If this is set, communication to the upstream will be via HTTP and not GRPC.",
            "properties": {
             "pathPrefix": {
              "description": "Sets a prefix to the value of authorization request header *Path*.",
              "type": "string"
             },
             "request": {
              "properties": {
               "allowedHeaders": {
                "description": "These headers will be copied from the incoming request to the request going\nto the auth server. Note that in addition to the user's supplied matchers:\n\n1. *Host*, *Method*, *Path* and *Content-Length* are automatically included to the list.\n\n2. *Content-Length* will be set to 0 and the request to the authorization service will not have\na message body.",
                "items": {
                 "type": "string"
                },
                "type": "array"
               },
               "headersToAdd": {
                "additionalProperties": {
                 "type": "string"
                },
                "description": "These headers that will be included to the request to authorization service. Note that\nclient request of the same key will be overridden.",
                "type": "object"
               }
              },
              "type": "object"
             },
             "response": {
              "properties": {
               "allowedClientHeaders": {
                "description": "When this. is set, authorization response headers that will be added to the client's response when auth request is denied.\nNote that when this list is *not* set, all the authorization response headers, except *Authority\n(Host)* will be in the response to the client. When a header is included in this list, *Path*,\n*Status*, *Content-Length*, *WWW-Authenticate* and *Location* are automatically added.",
                "items": {
                 "type": "string"
                },
                "type": "array"
               },
               "allowedUpstreamHeaders": {
                "description": "When this is set, authorization response headers that have a will be added to the original client request and sent to the upstream.\nNote that coexistent headers will be overridden.",
                "items": {
                 "type": "string"
                },
                "type": "array"
               }
              },
              "type": "object"
             }
            },
            "type": "object"
           },
           "requestBody": {
            "description": "Set this if you also want to send the body of the request, and not just the headers.",
            "properties": {
             "allowPartialMessage": {
              "description": "When this field is true, Envoy will buffer the message until *max_request_bytes* is reached.\nThe authorization request will be dispatched and no 413 HTTP error will be returned by the\nfilter.",
              "type": "boolean"
             },
             "maxRequestBytes": {
              "description": "Sets the maximum size of a message body that the filter will hold in memory. Envoy will return\n*HTTP 413* and will *not* initiate the authorization process when buffer reaches the number\nset in this field. Note that this setting will have precedence over failure_mode_allow.\nDefaults to 4KB.",
              "maximum": 4294967295,
              "minimum": 0,
              "type": "integer"
             },
             "packAsBytes": {
              "description": "When this field is true, Envoy will send the body sent to the external authorization service with raw bytes.",
              "type": "boolean"
             }
            },
            "type": "object"
           },
           "requestTimeout": {
            "description": "Timeout for the ext auth service to respond. Defaults to 2000ms. (OIDC requests to external IDPs can be slow)\nFor latency critical applications, this value should be tuned much lower, as extauth is on the request path.",
            "type": "string"
           },
           "statPrefix": {
            "description": "Optional additional prefix to use when emitting statistics.\nThis allows to distinguish emitted statistics between configured ext_authz filters in an HTTP filter chain.",
            "type": "string"
           },
           "statusOnError": {
            "description": "Sets the HTTP status that is returned to the client when there is a network error between the\nfilter and the authorization server. The default status is HTTP 403 Forbidden.\nIf set, this must be one of the following:\n- 100\n- 200 201 202 203 204 205 206 207 208 226\n- 300 301 302 303 304 305 307 308\n- 400 401 402 403 404 405 406 407 408 409 410 411 412 413 414 415 416 417 421 422 423 424 426 428 429 431\n- 500 501 502 503 504 505 506 507 508 510 511",
            "maximum": 4294967295,
            "minimum": 0,
            "type": "integer"
           },
           "transportApiVersion": {
            "description": "Determines the API version for the `ext_authz` transport protocol that will be used by Envoy\nto communicate with the auth server. Defaults to `V3`. For more info, see the `transport_api_version` field\n[here](https://www.envoyproxy.io/docs/envoy/latest/api-v3/extensions/filters/http/ext_authz/v3/ext_authz.proto#extensions-filters-http-ext-authz-v3-extauthz).",
            "enum": [
             "GLOO_MESH_AUTO",
             "ENVOY_AUTO",
             "V3"
            ],
            "type": "string"
           }
          },
          "type": "object"
         },
         "rateLimit": {
          "description": "Configure Global Rate limit options for clients connected to this Gateway.\nRate limits must be configured on specific routes in order to enable rate limiting for a Gateway.",
          "properties": {
           "denyOnFail": {
            "description": "Defaults to false",
            "type": "boolean"
           },
           "ratelimitServerRef": {
            "description": "The ratelimit service to ask about ratelimit decisions.\nThe provided ref will be used to search for a service of the given name/namespace on each cluster\nthat a Gateway will be created.\n\nIf omitted, Gloo Mesh will search for a service with the name rate-limiter in each namespace on each cluster\nthat a Gateway will be created.",
            "properties": {
             "name": {
              "description": "name of the resource being referenced",
              "type": "string"
             },
             "namespace": {
              "description": "namespace of the resource being referenced",
              "type": "string"
             }
            },
            "type": "object"
           },
           "requestTimeout": {
            "description": "Timeout for the ratelimit service to respond. Defaults to 100ms",
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       }
      },
      "type": "object"
     },
     "tcp": {
      "properties": {
       "options": {
        "description": "TCP Gateway configuration",
        "properties": {
         "tcpProxySettings": {
          "properties": {
           "idleTimeout": {
            "type": "string"
           },
           "maxConnectAttempts": {
            "description": "Contains various settings for Envoy's tcp proxy filter.\nSee here for more information: https://www.envoyproxy.io/docs/envoy/v1.10.0/api-v2/config/filter/network/tcp_proxy/v2/tcp_proxy.proto#envoy-api-msg-config-filter-network-tcp-proxy-v2-tcpproxy",
            "maximum": 4294967295,
            "minimum": 0,
            "nullable": true,
            "type": "integer"
           },
           "tunnelingConfig": {
            "description": "If set, this configures tunneling, e.g. configuration options to tunnel multiple TCP\npayloads over a shared HTTP tunnel. If this message is absent, the payload\nwill be proxied upstream as per usual.",
            "properties": {
             "hostname": {
              "description": "The hostname to send in the synthesized CONNECT headers to the upstream proxy.",
              "type": "string"
             }
            },
            "type": "object"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       },
       "tcpHosts": {
        "description": "TCP hosts that the gateway can route to",
        "items": {
         "properties": {
          "destination": {
           "oneOf": [
            {
             "not": {
              "anyOf": [
               {
                "required": [
                 "static"
                ]
               },
               {
                "required": [
                 "virtual"
                ]
               },
               {
                "required": [
                 "kube"
                ]
               },
               {
                "required": [
                 "forwardSniClusterName"
                ]
               }
              ]
             }
            },
            {
             "required": [
              "static"
             ]
            },
            {
             "required": [
              "virtual"
             ]
            },
            {
             "required": [
              "kube"
             ]
            },
            {
             "required": [
              "forwardSniClusterName"
             ]
            }
           ],
           "properties": {
            "forwardSniClusterName": {
             "description": "Forwards the request to a cluster name matching the TLS SNI name\nhttps://www.envoyproxy.io/docs/envoy/latest/api-v2/config/filter/network/sni_cluster/empty/sni_cluster\nNote: This filter will only work properly with TLS connections in which the upstream SNI domain is specified",
             "maxProperties": 0,
             "type": "object"
            },
            "kube": {
             "description": "Reference to a Kubernetes Service. Note that the service must exist in the same mesh or virtual mesh (with federation enabled) as\neach gateway workload which routes to this destination.",
             "properties": {
              "clusterName": {
               "description": "name of the cluster in which the resource exists",
               "type": "string"
              },
              "name": {
               "description": "name of the resource being referenced",
               "type": "string"
              },
              "namespace": {
               "description": "namespace of the resource being referenced",
               "type": "string"
              }
             },
             "type": "object"
            },
            "static": {
             "description": "Reference to a gloo mesh Static Destination",
             "properties": {
              "name": {
               "description": "name of the resource being referenced",
               "type": "string"
              },
              "namespace": {
               "description": "namespace of the resource being referenced",
               "type": "string"
              }
             },
             "type": "object"
            },
            "virtual": {
             "description": "Reference to a gloo mesh VirtualDestination",
             "properties": {
              "name": {
               "description": "name of the resource being referenced",
               "type": "string"
              },
              "namespace": {
               "description": "namespace of the resource being referenced",
               "type": "string"
              }
             },
             "type": "object"
            },
            "weight": {
             "description": "Relative weight of this destination to others in the same route.\nIf omitted, all destinations in the route will be load balanced between evenly.",
             "maximum": 4294967295,
             "minimum": 0,
             "type": "integer"
            }
           },
           "type": "object"
          },
          "name": {
           "description": "the logical name of the tcp host. names must be unique for each tcp host within a listener",
           "type": "string"
          }
         },
         "type": "object"
        },
        "type": "array"
       }
      },
      "type": "object"
     }
    },
    "type": "object"
   },
   "type": "array"
  },
  "ingressGatewaySelectors": {
   "description": "Select the destinations to deploy the gateway to.",
   "items": {
    "properties": {
     "destinationSelectors": {
      "description": "The set of Destinations that will be used as ingress gateways for external traffic entering the Mesh.\nIf omitted, a mesh-specific default ingress gateway destination will be used.\nFor Istio, any Kubernetes Service(s) with the label pair `{\"istio\": \"ingressgateway\"}` will be selected.",
      "items": {
       "properties": {
        "kubeServiceMatcher": {
         "description": "Match Kubernetes Services by their labels, namespaces, and/or clusters.",
         "properties": {
          "clusters": {
           "description": "If specified, match Kubernetes Services if they exist in one of the specified clusters.\n           When used in a networking policy, omission matches any cluster.\n           When used in a Gloo Mesh Role, a wildcard (`\"*\"`) must be specified to match any cluster.",
           "items": {
            "type": "string"
           },
           "type": "array"
          },
          "labels": {
           "additionalProperties": {
            "type": "string"
           },
           "description": "If specified, a match requires all labels to exist on a Kubernetes Service.\n           When used in a networking policy, omission matches any labels.\n           When used in a Gloo Mesh Role, a wildcard (`\"*\"`) must be specified to match any label key and/or value.",
           "type": "object"
          },
          "namespaces": {
