          },
          "required": [
           "resource"
          ],
          "type": "object"
         },
         "secretKeyRef": {
          "description": "Selects a key of a secret in the pod's namespace",
          "properties": {
           "key": {
            "description": "The key of the secret to select from.  Must be a valid secret key.",
            "type": "string"
