schema:
{
 "properties": {
  "configurations": {
   "description": "Custom configuration to be applied to the cluster.\n",
   "properties": {
    "sgBackupConfig": {
     "description": "Name of the [SGBackupConfig](https://stackgres.io/doc/latest/04-postgres-cluster-management/04-backups/) to use for this cluster. This will define the backups policy, storage and retention, among others, to apply to this cluster. If not set, a default backup config will be used.\n",
     "type": "string"
    },
    "sgPoolingConfig": {
     "description": "Name of the [SGPoolingConfig](https://stackgres.io/doc/latest/04-postgres-cluster-management/02-configuration-tuning/03-connection-pooling-configuration/) to use for this cluster. Each pod contains a sidecar with a connection pooler (currently: [PgBouncer](https://www.pgbouncer.org/)). The connection pooler is implemented as a sidecar.\n\nIf not selected, a default configuration will be used. Note that you may disable connection pooling altogether by setting to false the disableConnectionPooling property of the pods object.\n",
     "type": "string"
    },
    "sgPostgresConfig": {
     "description": "Name of the [SGPostgresConfig](https://stackgres.io/doc/latest/04-postgres-cluster-management/02-configuration-tuning/02-postgres-configuration/) to use for this cluster. It must exist. If not set, a default Postgres config, for the major version selected, will be used.\n",
     "type": "string"
    }
   },
   "type": "object"
  },
  "distributedLogs": {
   "description": "StackGres features a functionality for all pods to send Postgres, Patroni and PgBouncer logs to a central (distributed) location, which is in turn another Postgres database. Logs can then be accessed via SQL interface or from the web UI. This section controls whether to enable this feature or not. If not enabled, logs are send to the pod's standard output.",
   "properties": {
    "sgDistributedLogs": {
     "description": "Name of the [SGDistributedLogs](https://stackgres.io/doc/latest/04-postgres-cluster-management/06-distributed-logs/) to use for this cluster. It must exist.\n",
     "type": "string"
    }
   },
   "type": "object"
  },
  "initialData": {
   "description": "Cluster initialization data options. Cluster may be initialized empty, or from a backup restoration. You may also specify scripts to run on the database after cluster creation.",
   "properties": {
    "restore": {
     "properties": {
      "downloadDiskConcurrency": {
       "description": "The backup fetch process may fetch several streams in parallel. Specify a value larger than one for parallel fetching.\n",
       "minimum": 1,
       "type": "integer"
      },
      "fromBackup": {
       "description": "Initialize the cluster data by restoring an existing backup. If not used, cluster will be initialized empty. Set to the UID of an existing [SGBackup](https://stackgres.io/doc/latest/04-postgres-cluster-management/04-backups/) from which to restore the data. The backup needs to be in the same namespace.\n",
       "type": "string"
      }
     },
     "type": "object"
    },
    "scripts": {
     "description": "A list of SQL scripts that will be executed in sequence, exactly once, when the database is bootstrap and/or after restore is completed.\n",
     "items": {
      "description": "Scripts are executed in auto-commit mode with the user `postgres` in the specified database (or in database `postgres` if not specified).\n\nFields `script` and `scriptFrom` are mutually exclusive and only one of them is required.\n",
      "properties": {
       "database": {
        "description": "The database where the script will be executed. Defaults to the `postgres` database, if not specified.\n",
        "type": "string"
       },
       "name": {
        "description": "The name of the script. Must be unique across this SGCluster.\n",
        "type": "string"
       },
       "script": {
        "description": "The raw SQL script to execute. This field is mutually exclusive with `scriptFrom` field.\n",
        "type": "string"
       },
       "scriptFrom": {
        "description": "Reference to either a Kubernetes [Secret](https://kubernetes.io/docs/concepts/configuration/secret/) or a [ConfigMap](https://kubernetes.io/docs/concepts/configuration/configmap/) that contains the SQL script to execute. This field is mutually exclusive with `script` field.\n\nFields `secretKeyRef` and `configMapKeyRef` are mutually exclusive, and one of them is required.\n",
        "properties": {
         "configMapKeyRef": {
          "description": "A [ConfigMap](https://kubernetes.io/docs/concepts/configuration/configmap/) reference that contains the SQL script to execute. This field is mutually exclusive with `secretKeyRef` field.\n",
          "properties": {
           "key": {
            "description": "The key name within the ConfigMap that contains the SQL script to execute.\n",
            "type": "string"
           },
           "name": {
            "description": "The name of the ConfigMap that contains the SQL script to execute.\n",
            "type": "string"
           }
          },
          "type": "object"
         },
         "secretKeyRef": {
          "description": "A Kubernetes [SecretKeySelector](https://kubernetes.io/docs/reference/generated/kubernetes-api/v1.18/#secretkeyselector-v1-core) that contains the SQL script to execute. This field is mutually exclusive with `configMapKeyRef` field.\n",
          "properties": {
           "key": {
            "description": "The key of the secret to select from. Must be a valid secret key.",
            "type": "string"
           },
           "name": {
            "description": "Name of the referent. [More information](https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names).",
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       }
      },
      "type": "object"
     },
     "type": "array"
    }
   },
   "type": "object"
  },
  "instances": {
   "description": "Number of StackGres instances to be created. Each instance will contain one Postgres server. Out of all of the Postgres servers, one will be elected as the master, the rest will remain as read-only replicas.\n",
   "maximum": 16,
   "minimum": 1,
   "type": "integer"
  },
  "metadata": {
   "description": "StackGres cluster created resources metadata information.",
   "properties": {
    "annotations": {
     "description": "Custom Kubernetes [annotations](https://kubernetes.io/docs/concepts/overview/working-with-objects/annotations/) to be passed to resources created and managed by StackGres.",
     "properties": {
      "allResources": {
       "description": "Annotations to attach to any resource created or managed by StackGres.",
       "type": "object"
      },
      "pods": {
       "description": "Annotations to attach to pods created or managed by StackGres.",
       "type": "object"
      },
      "services": {
       "description": "Annotations to attach to services created or managed by StackGres.",
       "type": "object"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  },
  "nonProductionOptions": {
   "properties": {
    "disableClusterPodAntiAffinity": {
     "description": "It is a best practice, on non-containerized environments, when running production workloads, to run each database server on a different server (virtual or physical), i.e., not to co-locate more than one database server per host.\n\nThe same best practice applies to databases on containers. By default, StackGres will not allow to run more than one StackGres pod on a given K8s node. Set this property to true to allow more than one StackGres pod per node.\n",
     "type": "boolean"
    }
   },
   "type": "object"
  },
  "pods": {
   "description": "Cluster pod's configuration",
   "properties": {
    "disableConnectionPooling": {
     "description": "Set to `false` to avoid creating a connection pooling (using [PgBouncer](https://www.pgbouncer.org/)) sidecar.",
     "type": "boolean"
    },
    "disableMetricsExporter": {
     "description": "Set to `false` to avoid creating the Prometheus exporter sidecar. Recommended if you don't intent to use Prometheus for monitoring.",
     "type": "boolean"
    },
    "disablePostgresUtil": {
     "description": "Set to `false` to avoid creating the `postgres-util` sidecar. This sidecar contains usual Postgres administration utilities *that are not present in the main (`patroni`) container*, like `psql`. Only disable if you know what you are doing.",
     "type": "boolean"
    },
    "metadata": {
     "description": "Pod custom metadata information",
     "properties": {
      "labels": {
       "description": "Additional labels for StackGres Pods to have",
       "type": "object"
      }
     },
     "type": "object"
    },
    "persistentVolume": {
     "description": "Pod's persistent volume configuration",
     "properties": {
      "size": {
       "description": "Size of the PersistentVolume to be created for each instance of the cluster. Size must be specified either in Mebibytes, Gibibytes or Tebibytes (multiples of 2^20, 2^30 or 2^40, respectively).\n",
       "pattern": "^[0-9]+(\\.[0-9]+)?(Mi|Gi|Ti)$",
       "type": "string"
      },
      "storageClass": {
       "description": "Name of an existing StorageClass in the K8s cluster, that will be used to create the PersistentVolumes for the instances of this cluster.\n",
       "type": "string"
      }
     },
     "required": [
      "size"
     ],
     "type": "object"
    },
    "scheduling": {
     "description": "Pod custom scheduling configuration",
     "properties": {
      "nodeSelector": {
       "description": "Pod custom node selector",
       "type": "object"
      }
     },
     "type": "object"
    }
   },
   "required": [
    "persistentVolume"
   ],
   "type": "object"
  },
  "postgresServices": {
   "description": "Kubernetes [services](https://kubernetes.io/docs/concepts/services-networking/service/) created or managed by StackGres.",
   "properties": {
    "primary": {
     "description": "Configuration for the `-primary` service. It provides a stable connection (regardless of primary failures or switchovers) to the read-write Postgres server of the cluster.",
     "properties": {
      "annotations": {
       "description": "Custom Kubernetes [annotations](https://kubernetes.io/docs/concepts/overview/working-with-objects/annotations/) to be passed to the `-primary` service.",
       "type": "object"
      },
      "enabled": {
       "description": "Specify if the `-primary` service should be created or not.",
       "type": "boolean"
      },
      "type": {
       "description": "Specify the type of K8s service to create.",
       "enum": [
        "ClusterIP",
        "LoadBalancer",
        "NodePort"
       ],
       "type": "string"
      }
     },
     "type": "object"
    },
    "replicas": {
     "description": "Configuration for the `-replicas` service. It provides a stable connection (regardless of replica node failures) to any read-only Postgres server of the cluster. Read-only servers are load-balanced via this service.",
     "properties": {
      "annotations": {
       "description": "Custom Kubernetes [annotations](https://kubernetes.io/docs/concepts/overview/working-with-objects/annotations/) to be passed to the `-replicas` service.",
       "type": "object"
      },
      "enabled": {
       "description": "SSpecify if the `-replicas` service should be created or not.",
       "type": "boolean"
      },
      "type": {
       "description": "Specify the type of K8s service to create.",
       "enum": [
        "ClusterIP",
        "LoadBalancer",
        "NodePort"
       ],
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  },
  "postgresVersion": {
   "description": "Postgres version to use for the new cluster. It can be either of:\n*  The string 'latest', which will automatically select the latest major.minor Postgres version.\n*  A major version, like '12' or '11', which will select that major version and the latest minor version.\n*  A specific major.minor version, like '12.2'.\n",
   "type": "string"
  },
  "prometheusAutobind": {
   "description": "If enabled, a ServiceMonitor will be created for each Prometheus instance found in order to collect metrics.\n",
   "type": "boolean"
  },
  "sgInstanceProfile": {
   "description": "Name of the [SGInstanceProfile](https://stackgres.io/doc/latest/04-postgres-cluster-management/03-resource-profiles/) to use. A SGInstanceProfile defines CPU and memory limits. Must exist before creating the cluster. If you don't select a profile, a default (currently: 1 core, 2 GiB RAM) one will be used.\n",
   "type": "string"
  }
 },
 "required": [
  "instances",
  "postgresVersion",
  "pods"
 ],
 "title": "SG Cluster",
 "type": "object"
}
